function addtrackerlistener(obj){if(obj.addEventListener){obj.addEventListener('click',trackfiles,true);}else if(obj.attachEvent){obj.attachEvent("on"+'click',trackfiles);}}
function addmailtotrackerlistener(obj){if(obj.addEventListener){obj.addEventListener('click',trackmailto,true);}else if(obj.attachEvent){obj.attachEvent("on"+'click',trackmailto);}}
function trackfiles(array_element){if(array_element.srcElement&&array_element.srcElement.tagName!='IMG')
{var file_path="";var destination_host=(array_element.srcElement)?array_element.srcElement.hostname:this.hostname;if(location.host!=destination_host){file_path="/virtual/exlink/"+cleanURL(window.location,true)+'/'+((array_element.srcElement)?array_element.srcElement.hostname:this.hostname);file_path=file_path+((array_element.srcElement)?"/"+cleanURL(array_element.srcElement.pathname,false):this.pathname);}
else{file_path=((array_element.srcElement)?"/"+array_element.srcElement.pathname:this.pathname);var file_details=file_path.split('/');file_path=cleanURL(window.location,true)+'/'+file_details[(file_details.length-1)];file_path=(("/virtual/download/")+file_path);}
pageTracker._trackPageview(file_path);}}
function trackmailto(array_element){if(array_element.srcElement&&array_element.srcElement.tagName!='IMG')
{var email=((array_element.srcElement)?array_element.srcElement.href:this.href).substring(7);var url=cleanURL(window.location,true);var mail_path='/virtual/mailto/'+url+'/'+email;pageTracker._trackPageview(mail_path);}}
function cleanURL(url,end){var url=url.toString();var urlLen=url.length;if(end){if(url.charAt((urlLen-1))=='/')
url=url.substring(0,(urlLen-1));}
else{if(url.charAt(0)=='/')
url=url.substring(1,urlLen);}
return url;}
function trackPageview(view){if(typeof pageTracker=="undefined"){if(typeof _gaq=="undefined"){}else{_gaq.push(['_trackPageview',view]);}}else{pageTracker._trackPageview(view);}}